'use client';

import { useState } from 'react';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { 
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  Filter, 
  ArrowUpDown,
  X
} from 'lucide-react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter
} from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Responsavel } from '@/lib/types';
import { mockResponsaveis } from '@/lib/mockData';
import ResponsavelModal from '../../components/responsaveis/ResponsavelModal';

export default function ResponsaveisPage() {
  const [responsaveis, setResponsaveis] = useState<Responsavel[]>(mockResponsaveis);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedResponsavel, setSelectedResponsavel] = useState<Responsavel | null>(null);
  const [showResponsavelModal, setShowResponsavelModal] = useState(false);
  const [showFilterModal, setShowFilterModal] = useState(false);
  const [sortField, setSortField] = useState<keyof Responsavel | null>(null);
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');

  // Filter states
  const [filters, setFilters] = useState({
    nome: '',
    email: '',
    telefone: '',
    perfil: ''
  });
  const [activeFilters, setActiveFilters] = useState(filters);

  const handleSort = (field: keyof Responsavel) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const sortedResponsaveis = [...responsaveis].sort((a, b) => {
    if (!sortField) return 0;

    const aValue = a[sortField];
    const bValue = b[sortField];

    if (aValue === bValue) return 0;

    const direction = sortDirection === 'asc' ? 1 : -1;

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return aValue.localeCompare(bValue) * direction;
    }

    return 0;
  });

  const handleClearFilters = () => {
    const emptyFilters = {
      nome: '',
      email: '',
      telefone: '',
      perfil: ''
    };
    setFilters(emptyFilters);
    setActiveFilters(emptyFilters);
  };

  const handleApplyFilters = () => {
    setActiveFilters(filters);
    setShowFilterModal(false);
  };

  const filteredResponsaveis = sortedResponsaveis.filter(responsavel => {
    // Search query filter
    const matchesSearch = responsavel.dsNome.toLowerCase().includes(searchQuery.toLowerCase()) ||
      responsavel.dsEmail.toLowerCase().includes(searchQuery.toLowerCase()) ||
      responsavel.nmTelefone.toLowerCase().includes(searchQuery.toLowerCase());

    // Advanced filters
    const matchesNome = !activeFilters.nome ||
      responsavel.dsNome.toLowerCase().includes(activeFilters.nome.toLowerCase());

    const matchesEmail = !activeFilters.email ||
      responsavel.dsEmail.toLowerCase().includes(activeFilters.email.toLowerCase());

    const matchesTelefone = !activeFilters.telefone ||
      responsavel.nmTelefone.includes(activeFilters.telefone);

    const matchesPerfil = !activeFilters.perfil ||
      responsavel.dsPerfil === activeFilters.perfil;

    return matchesSearch && matchesNome && matchesEmail && matchesTelefone && matchesPerfil;
  });

  const hasActiveFilters = Object.values(activeFilters).some(value => value !== '');

  const handleCreateResponsavel = () => {
    setSelectedResponsavel(null);
    setShowResponsavelModal(true);
  };

  const handleEditResponsavel = (responsavel: Responsavel) => {
    setSelectedResponsavel(responsavel);
    setShowResponsavelModal(true);
  };

  const handleDeleteResponsavel = (id: string) => {
    if (window.confirm('Tem certeza que deseja excluir este responsável?')) {
      setResponsaveis(responsaveis.filter(responsavel => responsavel.idResponsavel !== id));
    }
  };

  const handleSaveResponsavel = (responsavel: Responsavel) => {
    if (selectedResponsavel) {
      // Update existing responsavel
      setResponsaveis(responsaveis.map(r => r.idResponsavel === responsavel.idResponsavel ? responsavel : r));
    } else {
      // Add new responsavel
      setResponsaveis([...responsaveis, responsavel]);
    }
    setShowResponsavelModal(false);
    setSelectedResponsavel(null);
  };

  return (
    <div className="flex flex-col min-h-0 flex-1">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">
            Cadastro de Responsáveis
          </h1>
          <Button 
            onClick={handleCreateResponsavel} 
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Novo Responsável
          </Button>
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Pesquisar por nome, email ou telefone..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 h-10 bg-gray-50 border-gray-200 focus:bg-white"
            />
          </div>
          <Button
            variant="secondary"
            className="h-10 px-4"
            onClick={() => setShowFilterModal(true)}
          >
            <Filter className="h-4 w-4 mr-2" />
            Filtrar
          </Button>
        </div>
      </div>

      {/* Table */}
      <div className="flex-1 overflow-auto">
        <Table>
          <TableHeader className="bg-gray-50">
            <TableRow>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsNome')}>
                <div className="flex items-center">
                  Nome
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsEmail')}>
                <div className="flex items-center">
                  Email
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nmTelefone')}>
                <div className="flex items-center">
                  Telefone
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="w-24 text-right">Ações</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredResponsaveis.length === 0 ? (
              <TableRow>
                <TableCell colSpan={4} className="text-center py-8 text-gray-500">
                  Nenhum responsável encontrado
                </TableCell>
              </TableRow>
            ) : (
              filteredResponsaveis.map((responsavel) => (
                <TableRow key={responsavel.idResponsavel} className="hover:bg-gray-50">
                  <TableCell className="font-medium">{responsavel.dsNome}</TableCell>
                  <TableCell>{responsavel.dsEmail}</TableCell>
                  <TableCell>{responsavel.nmTelefone}</TableCell>
                  <TableCell className="text-right">
                    <div className="flex justify-end space-x-2">
                      <Button 
                        variant="ghost" 
                        size="icon" 
                        onClick={() => handleEditResponsavel(responsavel)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button 
                        variant="ghost" 
                        size="icon" 
                        onClick={() => handleDeleteResponsavel(responsavel.idResponsavel)}
                        className="text-red-500 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>

      {/* Responsavel Modal */}
      {showResponsavelModal && (
        <ResponsavelModal
          responsavel={selectedResponsavel}
          onClose={() => {
            setShowResponsavelModal(false);
            setSelectedResponsavel(null);
          }}
          onSave={handleSaveResponsavel}
        />
      )}

      {/* Filter Modal */}
      {showFilterModal && (
        <Dialog open={showFilterModal} onOpenChange={setShowFilterModal}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Filtrar Responsáveis</DialogTitle>
            </DialogHeader>
            <div className="grid gap-4">
              <div>
                <Label htmlFor="nome">Nome</Label>
                <Input
                  id="nome"
                  value={filters.nome}
                  onChange={(e) => setFilters({ ...filters, nome: e.target.value })}
                  placeholder="Filtrar por nome"
                />
              </div>
              <div>
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  value={filters.email}
                  onChange={(e) => setFilters({ ...filters, email: e.target.value })}
                  placeholder="Filtrar por email"
                />
              </div>
              <div>
                <Label htmlFor="telefone">Telefone</Label>
                <Input
                  id="telefone"
                  value={filters.telefone}
                  onChange={(e) => setFilters({ ...filters, telefone: e.target.value })}
                  placeholder="Filtrar por telefone"
                />
              </div>
              <div>
                <Label htmlFor="perfil">Perfil</Label>
                <Select
                  id="perfil"
                  value={filters.perfil}
                  onValueChange={(value) => setFilters({ ...filters, perfil: value })}
                  placeholder="Selecione um perfil"
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Selecione um perfil" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="admin">Admin</SelectItem>
                    <SelectItem value="user">User</SelectItem>
                    <SelectItem value="manager">Manager</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </DialogContent>
          <DialogFooter>
            <Button
              variant="secondary"
              onClick={() => {
                setShowFilterModal(false);
                handleClearFilters();
              }}
              className="mr-2"
            >
              <X className="h-4 w-4 mr-2" />
              Limpar Filtros
            </Button>
            <Button onClick={handleApplyFilters}>
              Aplicar Filtros
            </Button>
          </DialogFooter>
        </Dialog>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { 
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  Filter, 
  ArrowUpDown,
  X
} from 'lucide-react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter
} from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Responsavel } from '@/lib/types';
import { mockResponsaveis } from '@/lib/mockData';
import ResponsavelModal from '../../components/responsaveis/ResponsavelModal';

export default function ResponsaveisPage() {
  const [responsaveis, setResponsaveis] = useState<Responsavel[]>(mockResponsaveis);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedResponsavel, setSelectedResponsavel] = useState<Responsavel | null>(null);
  const [showResponsavelModal, setShowResponsavelModal] = useState(false);
  const [showFilterModal, setShowFilterModal] = useState(false);
  const [sortField, setSortField] = useState<keyof Responsavel | null>(null);
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');

  // Filter states
  const [filters, setFilters] = useState({
    nome: '',
    email: '',
    telefone: '',
    perfil: ''
  });
  const [activeFilters, setActiveFilters] = useState(filters);

  const handleSort = (field: keyof Responsavel) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const sortedResponsaveis = [...responsaveis].sort((a, b) => {
    if (!sortField) return 0;

    const aValue = a[sortField];
    const bValue = b[sortField];

    if (aValue === bValue) return 0;

    const direction = sortDirection === 'asc' ? 1 : -1;

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return aValue.localeCompare(bValue) * direction;
    }

    return 0;
  });

  const handleClearFilters = () => {
    const emptyFilters = {
      nome: '',
      email: '',
      telefone: '',
      perfil: ''
    };
    setFilters(emptyFilters);
    setActiveFilters(emptyFilters);
  };

  const handleApplyFilters = () => {
    setActiveFilters(filters);
    setShowFilterModal(false);
  };

  const filteredResponsaveis = sortedResponsaveis.filter(responsavel => {
    // Search query filter
    const matchesSearch = responsavel.dsNome.toLowerCase().includes(searchQuery.toLowerCase()) ||
      responsavel.dsEmail.toLowerCase().includes(searchQuery.toLowerCase()) ||
      responsavel.nmTelefone.toLowerCase().includes(searchQuery.toLowerCase());

    // Advanced filters
    const matchesNome = !activeFilters.nome ||
      responsavel.dsNome.toLowerCase().includes(activeFilters.nome.toLowerCase());

    const matchesEmail = !activeFilters.email ||
      responsavel.dsEmail.toLowerCase().includes(activeFilters.email.toLowerCase());

    const matchesTelefone = !activeFilters.telefone ||
      responsavel.nmTelefone.includes(activeFilters.telefone);

    const matchesPerfil = !activeFilters.perfil ||
      responsavel.dsPerfil === activeFilters.perfil;

    return matchesSearch && matchesNome && matchesEmail && matchesTelefone && matchesPerfil;
  });

  const hasActiveFilters = Object.values(activeFilters).some(value => value !== '');

  const handleCreateResponsavel = () => {
    setSelectedResponsavel(null);
    setShowResponsavelModal(true);
  };

  const handleEditResponsavel = (responsavel: Responsavel) => {
    setSelectedResponsavel(responsavel);
    setShowResponsavelModal(true);
  };

  const handleDeleteResponsavel = (id: string) => {
    if (window.confirm('Tem certeza que deseja excluir este responsável?')) {
      setResponsaveis(responsaveis.filter(responsavel => responsavel.idResponsavel !== id));
    }
  };

  const handleSaveResponsavel = (responsavel: Responsavel) => {
    if (selectedResponsavel) {
      // Update existing responsavel
      setResponsaveis(responsaveis.map(r => r.idResponsavel === responsavel.idResponsavel ? responsavel : r));
    } else {
      // Add new responsavel
      setResponsaveis([...responsaveis, responsavel]);
    }
    setShowResponsavelModal(false);
    setSelectedResponsavel(null);
  };

  return (
    <div className="flex flex-col min-h-0 flex-1">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">
            Cadastro de Responsáveis
          </h1>
          <Button 
            onClick={handleCreateResponsavel} 
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Novo Responsável
          </Button>
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Pesquisar por nome, email ou telefone..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 h-10 bg-gray-50 border-gray-200 focus:bg-white"
            />
          </div>
          <Button
            variant="secondary"
            className="h-10 px-4"
            onClick={() => setShowFilterModal(true)}
          >
            <Filter className="h-4 w-4 mr-2" />
            Filtrar
          </Button>
        </div>
      </div>

      {/* Table */}
      <div className="flex-1 overflow-auto">
        <Table>
          <TableHeader className="bg-gray-50">
            <TableRow>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsNome')}>
                <div className="flex items-center">
                  Nome
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsEmail')}>
                <div className="flex items-center">
                  Email
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nmTelefone')}>
                <div className="flex items-center">
                  Telefone
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="w-24 text-right">Ações</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredResponsaveis.length === 0 ? (
              <TableRow>
                <TableCell colSpan={4} className="text-center py-8 text-gray-500">
                  Nenhum responsável encontrado
                </TableCell>
              </TableRow>
            ) : (
              filteredResponsaveis.map((responsavel) => (
                <TableRow key={responsavel.idResponsavel} className="hover:bg-gray-50">
                  <TableCell className="font-medium">{responsavel.dsNome}</TableCell>
                  <TableCell>{responsavel.dsEmail}</TableCell>
                  <TableCell>{responsavel.nmTelefone}</TableCell>
                  <TableCell className="text-right">
                    <div className="flex justify-end space-x-2">
                      <Button 
                        variant="ghost" 
                        size="icon" 
                        onClick={() => handleEditResponsavel(responsavel)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button 
                        variant="ghost" 
                        size="icon" 
                        onClick={() => handleDeleteResponsavel(responsavel.idResponsavel)}
                        className="text-red-500 hover:text-red-700"
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </TableCell>
                </TableRow>
              ))
            )}
          </TableBody>
        </Table>
      </div>

      {/* Responsavel Modal */}
      {showResponsavelModal && (
        <ResponsavelModal
          responsavel={selectedResponsavel}
          onClose={() => {
            setShowResponsavelModal(false);
            setSelectedResponsavel(null);
          }}
          onSave={handleSaveResponsavel}
        />
      )}

      {/* Filter Modal */}
      {showFilterModal && (
        <Dialog open={showFilterModal} onOpenChange={setShowFilterModal}>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Filtrar Responsáveis</DialogTitle>
            </DialogHeader>
            <div className="grid gap-4">
              <div>
                <Label htmlFor="nome">Nome</Label>
                <Input
                  id="nome"
                  value={filters.nome}
                  onChange={(e) => setFilters({ ...filters, nome: e.target.value })}
                  placeholder="Filtrar por nome"
                />
              </div>
              <div>
                <Label htmlFor="email">Email</Label>
                <Input
                  id="email"
                  value={filters.email}
                  onChange={(e) => setFilters({ ...filters, email: e.target.value })}
                  placeholder="Filtrar por email"
                />
              </div>
              <div>
                <Label htmlFor="telefone">Telefone</Label>
                <Input
                  id="telefone"
                  value={filters.telefone}
                  onChange={(e) => setFilters({ ...filters, telefone: e.target.value })}
                  placeholder="Filtrar por telefone"
                />
              </div>
              <div>
                <Label htmlFor="perfil">Perfil</Label>
                <Select
                  value={filters.perfil}
                  onValueChange={(value) => setFilters({ ...filters, perfil: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Selecione um perfil" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="admin">Admin</SelectItem>
                    <SelectItem value="user">User</SelectItem>
                    <SelectItem value="manager">Manager</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </DialogContent>
          <DialogFooter>
            <Button
              variant="secondary"
              onClick={() => {
                setShowFilterModal(false);
                handleClearFilters();
              }}
              className="mr-2"
            >
              <X className="h-4 w-4 mr-2" />
              Limpar Filtros
            </Button>
            <Button onClick={handleApplyFilters}>
              Aplicar Filtros
            </Button>
          </DialogFooter>
        </Dialog>
      )}
    </div>
  );
}

'use client'

import { useState } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { Checkbox } from '@/components/ui/checkbox'
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table'
import { 
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  Filter, 
  ArrowUpDown,
  X
} from 'lucide-react'
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter
} from '@/components/ui/dialog'
import { Label } from '@/components/ui/label'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Tema } from '@/lib/types'
import { mockTemas, mockAreas } from '@/lib/mockData'
import { TemaModal } from '@/components/temas/TemaModal'

export default function TemasPage() {
  const [temas, setTemas] = useState<Tema[]>(mockTemas)
  const [searchQuery, setSearchQuery] = useState('')
  const [selectedTema, setSelectedTema] = useState<Tema | null>(null)
  const [showTemaModal, setShowTemaModal] = useState(false)
  const [sortField, setSortField] = useState<keyof Tema | null>(null)
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc')
  const [showFilterModal, setShowFilterModal] = useState(false)
  const [filterAreas, setFilterAreas] = useState<string[]>([])
  const [filterTpContagem, setFilterTpContagem] = useState<string | null>(null)

  const handleSort = (field: keyof Tema) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc')
    } else {
      setSortField(field)
      setSortDirection('asc')
    }
  }

  const sortedTemas = [...temas].sort((a, b) => {
    if (!sortField) return 0

    const aValue = a[sortField]
    const bValue = b[sortField]

    if (aValue === bValue) return 0

    const direction = sortDirection === 'asc' ? 1 : -1

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return aValue.localeCompare(bValue) * direction
    }

    if (Array.isArray(aValue) && Array.isArray(bValue)) {
      return (aValue.length - bValue.length) * direction
    }

    if (aValue < bValue) return -1 * direction
    return 1 * direction
  })

  const getAreasForTema = (idAreas: string[]) => {
    return mockAreas.filter(area => idAreas.includes(area.idArea))
  }

  const filteredTemas = sortedTemas.filter(tema =>
    tema.nmTema.toLowerCase().includes(searchQuery.toLowerCase()) ||
    tema.dsTema.toLowerCase().includes(searchQuery.toLowerCase())
  ).filter(tema => {
    const areas = getAreasForTema(tema.idAreas)
    const areaMatch = filterAreas.length === 0 || areas.some(area => filterAreas.includes(area.idArea))
    const tpContagemMatch = !filterTpContagem || tema.tpContagem === filterTpContagem
    return areaMatch && tpContagemMatch
  })


  const handleCreateTema = () => {
    setSelectedTema(null)
    setShowTemaModal(true)
  }

  const handleEditTema = (tema: Tema) => {
    setSelectedTema(tema)
    setShowTemaModal(true)
  }

  const handleDeleteTema = (temaId: string) => {
    if (window.confirm('Tem certeza que deseja excluir este tema?')) {
      setTemas(prev => prev.filter(tema => tema.idTema !== temaId))
    }
  }

  const handleSaveTema = (tema: Tema) => {
    if (selectedTema) {
      // Update existing tema
      setTemas(prev => prev.map(t => t.idTema === tema.idTema ? tema : t))
    } else {
      // Add new tema
      setTemas(prev => [...prev, tema])
    }
    setShowTemaModal(false)
    setSelectedTema(null)
  }

  const handleFilterSubmit = () => {
    setShowFilterModal(false)
  }

  return (
    <div className="flex flex-col min-h-0 flex-1">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">
            Cadastro de Temas
          </h1>
          <Button 
            onClick={handleCreateTema} 
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Novo Tema
          </Button>
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Pesquisar por nome ou descrição..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 h-10 bg-gray-50 border-gray-200 focus:bg-white"
            />
          </div>
          <Button
            variant="secondary"
            className="h-10 px-4"
            onClick={() => setShowFilterModal(true)}
          >
            <Filter className="h-4 w-4 mr-2" />
            Filtrar
          </Button>
        </div>
      </div>

      {/* Table */}
      <div className="flex-1 overflow-auto">
        <Table>
          <TableHeader className="bg-gray-50">
            <TableRow>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nmTema')}>
                <div className="flex items-center">
                  Nome
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsTema')}>
                <div className="flex items-center">
                  Descrição
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('idAreas')}>
                <div className="flex items-center">
                  Áreas Relacionadas
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nrDiasPrazo')}>
                <div className="flex items-center">
                  Prazo (dias)
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('tpContagem')}>
                <div className="flex items-center">
                  Tipo de Contagem
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dtCadastro')}>
                <div className="flex items-center">
                  Data de Cadastro
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="w-24 text-right">Ações</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredTemas.length === 0 ? (
              <TableRow>
                <TableCell colSpan={7} className="text-center py-8 text-gray-500">
                  Nenhum tema encontrado
                </TableCell>
              </TableRow>
            ) : (
              filteredTemas.map((tema) => {
                const areas = getAreasForTema(tema.idAreas)
                return (
                  <TableRow key={tema.idTema} className="hover:bg-gray-50">
                    <TableCell className="font-medium">{tema.nmTema}</TableCell>
                    <TableCell className="max-w-xs">
                      <div className="truncate" title={tema.dsTema}>
                        {tema.dsTema}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex flex-wrap gap-1 max-w-sm">
                        {areas.length === 0 ? (
                          <span className="text-gray-400 text-sm">Nenhuma área</span>
                        ) : (
                          areas.map((area) => (
                            <Badge key={area.idArea} variant="outline" className="text-xs">
                              {area.nmArea}
                            </Badge>
                          ))
                        )}
                      </div>
                    </TableCell>
                    <TableCell>{tema.nrDiasPrazo}</TableCell>
                    <TableCell>
                      <Badge variant={tema.tpContagem === 'UTEIS' ? 'default' : 'secondary'}>
                        {tema.tpContagem === 'UTEIS' ? 'Úteis' : 'Corridos'}
                      </Badge>
                    </TableCell>
                    <TableCell>{new Date(tema.dtCadastro).toLocaleDateString('pt-BR')}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end space-x-2">
                        <Button 
                          variant="ghost" 
                          size="icon" 
                          onClick={() => handleEditTema(tema)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button 
                          variant="ghost" 
                          size="icon" 
                          onClick={() => handleDeleteTema(tema.idTema)}
                          className="text-red-500 hover:text-red-700"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                )
              })
            )}
          </TableBody>
        </Table>
      </div>

      {/* Tema Modal */}
      {showTemaModal && (
        <TemaModal
          isOpen={showTemaModal}
          onClose={() => {
            setShowTemaModal(false)
            setSelectedTema(null)
          }}
          onSave={handleSaveTema}
          tema={selectedTema}
        />
      )}

      {/* Filter Modal */}
      {showFilterModal && (
        <Dialog open={showFilterModal} onOpenChange={setShowFilterModal}>
          <DialogContent className="max-w-sm p-6">
            <DialogHeader>
              <DialogTitle className="text-lg font-bold">
                Filtrar Temas
              </DialogTitle>
            </DialogHeader>

            <div>
              <Label htmlFor="areas" className="block text-sm font-medium text-gray-700">
                Áreas Relacionadas
              </Label>
              <div className="mt-2">
                {mockAreas.map(area => (
                  <div key={area.idArea} className="flex items-center">
                    <Checkbox
                      id={`area-${area.idArea}`}
                      checked={filterAreas.includes(area.idArea)}
                      onCheckedChange={(checked) => {
                        if (checked) {
                          setFilterAreas(prev => [...prev, area.idArea])
                        } else {
                          setFilterAreas(prev => prev.filter(id => id !== area.idArea))
                        }
                      }}
                      className="h-4 w-4 text-blue-600 border-gray-300 rounded"
                    />
                    <Label htmlFor={`area-${area.idArea}`} className="ml-2 text-sm cursor-pointer">
                      {area.nmArea}
                    </Label>
                  </div>
                ))}
              </div>
            </div>

            <div>
              <Label htmlFor="tpContagem" className="block text-sm font-medium text-gray-700">
                Tipo de Contagem
              </Label>
              <Select
                id="tpContagem"
                value={filterTpContagem?.toString()}
                onValueChange={setFilterTpContagem}
                className="mt-2"
              >
                <SelectTrigger>
                  <SelectValue placeholder="Selecione o tipo de contagem" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="UTEIS">Úteis</SelectItem>
                  <SelectItem value="CORRIDOS">Corridos</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <DialogFooter>
              <Button
                onClick={handleFilterSubmit}
                className="bg-blue-600 hover:bg-blue-700"
              >
                Aplicar Filtros
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}
    </div>
  )
}

'use client'

import { useState } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import { Checkbox } from '@/components/ui/checkbox'
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table'
import { 
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  Filter, 
  ArrowUpDown,
  X
} from 'lucide-react'
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter
} from '@/components/ui/dialog'
import { Label } from '@/components/ui/label'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Tema } from '@/lib/types'
import { mockTemas, mockAreas } from '@/lib/mockData'
import { TemaModal } from '@/components/temas/TemaModal'

export default function TemasPage() {
  const [temas, setTemas] = useState<Tema[]>(mockTemas)
  const [searchQuery, setSearchQuery] = useState('')
  const [selectedTema, setSelectedTema] = useState<Tema | null>(null)
  const [showTemaModal, setShowTemaModal] = useState(false)
  const [sortField, setSortField] = useState<keyof Tema | null>(null)
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc')
  const [showFilterModal, setShowFilterModal] = useState(false)
  const [filterAreas, setFilterAreas] = useState<string[]>([])
  const [filterTpContagem, setFilterTpContagem] = useState<string | null>(null)

  const handleSort = (field: keyof Tema) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc')
    } else {
      setSortField(field)
      setSortDirection('asc')
    }
  }

  const sortedTemas = [...temas].sort((a, b) => {
    if (!sortField) return 0

    const aValue = a[sortField]
    const bValue = b[sortField]

    if (aValue === bValue) return 0

    const direction = sortDirection === 'asc' ? 1 : -1

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return aValue.localeCompare(bValue) * direction
    }

    if (Array.isArray(aValue) && Array.isArray(bValue)) {
      return (aValue.length - bValue.length) * direction
    }

    if (aValue < bValue) return -1 * direction
    return 1 * direction
  })

  const getAreasForTema = (idAreas: string[]) => {
    return mockAreas.filter(area => idAreas.includes(area.idArea))
  }

  const filteredTemas = sortedTemas.filter(tema =>
    tema.nmTema.toLowerCase().includes(searchQuery.toLowerCase()) ||
    tema.dsTema.toLowerCase().includes(searchQuery.toLowerCase())
  ).filter(tema => {
    const areas = getAreasForTema(tema.idAreas)
    const areaMatch = filterAreas.length === 0 || areas.some(area => filterAreas.includes(area.idArea))
    const tpContagemMatch = !filterTpContagem || tema.tpContagem === filterTpContagem
    return areaMatch && tpContagemMatch
  })


  const handleCreateTema = () => {
    setSelectedTema(null)
    setShowTemaModal(true)
  }

  const handleEditTema = (tema: Tema) => {
    setSelectedTema(tema)
    setShowTemaModal(true)
  }

  const handleDeleteTema = (temaId: string) => {
    if (window.confirm('Tem certeza que deseja excluir este tema?')) {
      setTemas(prev => prev.filter(tema => tema.idTema !== temaId))
    }
  }

  const handleSaveTema = (tema: Tema) => {
    if (selectedTema) {
      // Update existing tema
      setTemas(prev => prev.map(t => t.idTema === tema.idTema ? tema : t))
    } else {
      // Add new tema
      setTemas(prev => [...prev, tema])
    }
    setShowTemaModal(false)
    setSelectedTema(null)
  }

  const handleFilterSubmit = () => {
    setShowFilterModal(false)
  }

  return (
    <div className="flex flex-col min-h-0 flex-1">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">
            Cadastro de Temas
          </h1>
          <Button 
            onClick={handleCreateTema} 
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Novo Tema
          </Button>
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Pesquisar por nome ou descrição..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 h-10 bg-gray-50 border-gray-200 focus:bg-white"
            />
          </div>
          <Button
            variant="secondary"
            className="h-10 px-4"
            onClick={() => setShowFilterModal(true)}
          >
            <Filter className="h-4 w-4 mr-2" />
            Filtrar
          </Button>
        </div>
      </div>

      {/* Table */}
      <div className="flex-1 overflow-auto">
        <Table>
          <TableHeader className="bg-gray-50">
            <TableRow>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nmTema')}>
                <div className="flex items-center">
                  Nome
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsTema')}>
                <div className="flex items-center">
                  Descrição
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('idAreas')}>
                <div className="flex items-center">
                  Áreas Relacionadas
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nrDiasPrazo')}>
                <div className="flex items-center">
                  Prazo (dias)
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('tpContagem')}>
                <div className="flex items-center">
                  Tipo de Contagem
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dtCadastro')}>
                <div className="flex items-center">
                  Data de Cadastro
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="w-24 text-right">Ações</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredTemas.length === 0 ? (
              <TableRow>
                <TableCell colSpan={7} className="text-center py-8 text-gray-500">
                  Nenhum tema encontrado
                </TableCell>
              </TableRow>
            ) : (
              filteredTemas.map((tema) => {
                const areas = getAreasForTema(tema.idAreas)
                return (
                  <TableRow key={tema.idTema} className="hover:bg-gray-50">
                    <TableCell className="font-medium">{tema.nmTema}</TableCell>
                    <TableCell className="max-w-xs">
                      <div className="truncate" title={tema.dsTema}>
                        {tema.dsTema}
                      </div>
                    </TableCell>
                    <TableCell>
                      <div className="flex flex-wrap gap-1 max-w-sm">
                        {areas.length === 0 ? (
                          <span className="text-gray-400 text-sm">Nenhuma área</span>
                        ) : (
                          areas.map((area) => (
                            <Badge key={area.idArea} variant="outline" className="text-xs">
                              {area.nmArea}
                            </Badge>
                          ))
                        )}
                      </div>
                    </TableCell>
                    <TableCell>{tema.nrDiasPrazo}</TableCell>
                    <TableCell>
                      <Badge variant={tema.tpContagem === 'UTEIS' ? 'default' : 'secondary'}>
                        {tema.tpContagem === 'UTEIS' ? 'Úteis' : 'Corridos'}
                      </Badge>
                    </TableCell>
                    <TableCell>{new Date(tema.dtCadastro).toLocaleDateString('pt-BR')}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end space-x-2">
                        <Button 
                          variant="ghost" 
                          size="icon" 
                          onClick={() => handleEditTema(tema)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button 
                          variant="ghost" 
                          size="icon" 
                          onClick={() => handleDeleteTema(tema.idTema)}
                          className="text-red-500 hover:text-red-700"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                )
              })
            )}
          </TableBody>
        </Table>
      </div>

      {/* Tema Modal */}
      {showTemaModal && (
        <TemaModal
          isOpen={showTemaModal}
          onClose={() => {
            setShowTemaModal(false)
            setSelectedTema(null)
          }}
          onSave={handleSaveTema}
          tema={selectedTema}
        />
      )}

      {/* Filter Modal */}
      {showFilterModal && (
        <Dialog open={showFilterModal} onOpenChange={setShowFilterModal}>
          <DialogContent className="max-w-sm p-6">
            <DialogHeader>
              <DialogTitle className="text-lg font-bold">
                Filtrar Temas
              </DialogTitle>
            </DialogHeader>

            <div>
              <Label htmlFor="areas" className="block text-sm font-medium text-gray-700">
                Áreas Relacionadas
              </Label>
              <div className="mt-2">
                {mockAreas.map(area => (
                  <div key={area.idArea} className="flex items-center">
                    <Checkbox
                      id={`area-${area.idArea}`}
                      checked={filterAreas.includes(area.idArea)}
                      onCheckedChange={(checked) => {
                        if (checked) {
                          setFilterAreas(prev => [...prev, area.idArea])
                        } else {
                          setFilterAreas(prev => prev.filter(id => id !== area.idArea))
                        }
                      }}
                      className="h-4 w-4 text-blue-600 border-gray-300 rounded"
                    />
                    <Label htmlFor={`area-${area.idArea}`} className="ml-2 text-sm cursor-pointer">
                      {area.nmArea}
                    </Label>
                  </div>
                ))}
              </div>
            </div>

            <div>
              <Label htmlFor="tpContagem" className="block text-sm font-medium text-gray-700">
                Tipo de Contagem
              </Label>
              <Select
                value={filterTpContagem?.toString()}
                onValueChange={setFilterTpContagem}
              >
                <SelectTrigger className="mt-2">
                  <SelectValue placeholder="Selecione o tipo de contagem" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="UTEIS">Úteis</SelectItem>
                  <SelectItem value="CORRIDOS">Corridos</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <DialogFooter>
              <Button
                onClick={handleFilterSubmit}
                className="bg-blue-600 hover:bg-blue-700"
              >
                Aplicar Filtros
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}
    </div>
  )
}

'use client';

import { useState } from 'react';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import {
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  Filter, 
  ArrowUpDown,
  MoreHorizontal
} from 'lucide-react';
import { Tema } from '@/lib/types';
import {mockTemas, mockAreas, getAreaNameById} from '@/lib/mockData';
import { TemaModal } from './TemaModal';
import {DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger} from "@/components/ui/dropdown-menu";

export default function TemasTable() {
  const [temas, setTemas] = useState<Tema[]>(mockTemas);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedTema, setSelectedTema] = useState<Tema | null>(null);
  const [showTemaModal, setShowTemaModal] = useState(false);
  const [sortField, setSortField] = useState<keyof Tema | null>(null);
  const [sortDirection, setSortDirection] = useState<'asc' | 'desc'>('asc');

  const handleSort = (field: keyof Tema) => {
    if (sortField === field) {
      setSortDirection(sortDirection === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortDirection('asc');
    }
  };

  const sortedTemas = [...temas].sort((a, b) => {
    if (!sortField) return 0;

    const aValue = a[sortField];
    const bValue = b[sortField];

    if (aValue === bValue) return 0;

    const direction = sortDirection === 'asc' ? 1 : -1;

    if (typeof aValue === 'string' && typeof bValue === 'string') {
      return aValue.localeCompare(bValue) * direction;
    }

    if (aValue < bValue) return -1 * direction;
    return 1 * direction;
  });

  const filteredTemas = sortedTemas.filter(tema => 
    tema.dsTema.toLowerCase().includes(searchQuery.toLowerCase()) ||
    tema.nmTema.toLowerCase().includes(searchQuery.toLowerCase()) ||
    tema.idAreas.some(areaId => getAreaNameById(areaId).toLowerCase().includes(searchQuery.toLowerCase()))
  );

  const handleCreateTema = () => {
    setSelectedTema(null);
    setShowTemaModal(true);
  };

  const handleEditTema = (tema: Tema) => {
    setSelectedTema(tema);
    setShowTemaModal(true);
  };

  const handleDeleteTema = (id: string) => {
    if (window.confirm('Tem certeza que deseja excluir este tema?')) {
      setTemas(temas.filter(tema => tema.idTema !== id));
    }
  };

  const handleSaveTema = (tema: Tema) => {
    if (selectedTema) {
      // Update existing tema
      setTemas(temas.map(t => t.idTema === tema.idTema ? tema : t));
    } else {
      // Add new tema
      setTemas([...temas, tema]);
    }
    setShowTemaModal(false);
    setSelectedTema(null);
  };

  const getAreasForTema = (idAreas: string[]) => {
    return mockAreas.filter(area => idAreas.includes(area.idArea));
  };

  return (
    <div className="h-full flex flex-col bg-gray-50">
      {/* Header */}
      <div className="bg-white border-b border-gray-200 p-6">
        <div className="flex items-center justify-between mb-4">
          <h1 className="text-2xl font-bold text-gray-900">
            Cadastro de Temas
          </h1>
          <Button 
            onClick={handleCreateTema} 
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="h-4 w-4 mr-2" />
            Novo Tema
          </Button>
        </div>

        <div className="flex items-center space-x-4">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input
              placeholder="Pesquisar por nome, número ou área..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10 h-10 bg-gray-50 border-gray-200 focus:bg-white"
            />
          </div>
          <Button variant="secondary" className="h-10 px-4">
            <Filter className="h-4 w-4 mr-2" />
            Filtrar
          </Button>
        </div>
      </div>

      {/* Table */}
      <div className="flex-1 overflow-auto">
        <Table>
          <TableHeader className="bg-gray-50">
            <TableRow>
              <TableHead className="w-16 cursor-pointer" onClick={() => handleSort('nmTema')}>
                <div className="flex items-center">
                  Área
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dsTema')}>
                <div className="flex items-center">
                  Nome
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('idAreas')}>
                <div className="flex items-center">
                  Áreas Relacionadas
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('nrDiasPrazo')}>
                <div className="flex items-center">
                  Prazo (dias)
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('tpContagem')}>
                <div className="flex items-center">
                  Tipo de Contagem
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="cursor-pointer" onClick={() => handleSort('dtCadastro')}>
                <div className="flex items-center">
                  Data de Cadastro
                  <ArrowUpDown className="ml-2 h-4 w-4" />
                </div>
              </TableHead>
              <TableHead className="w-24 text-right">Ações</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredTemas.length === 0 ? (
              <TableRow>
                <TableCell colSpan={7} className="text-center py-8 text-gray-500">
                  Nenhum tema encontrado
                </TableCell>
              </TableRow>
            ) : (
              filteredTemas.map((tema) => {
                const areas = getAreasForTema(tema.idAreas);
                return (
                  <TableRow key={tema.idTema} className="hover:bg-gray-50">
                    <TableCell className="font-medium">{tema.nmTema}</TableCell>
                    <TableCell>{tema.dsTema}</TableCell>
                    <TableCell>
                      <div className="flex flex-wrap gap-1 max-w-sm">
                        {areas.length === 0 ? (
                          <span className="text-muted-foreground text-sm">Nenhuma área</span>
                        ) : (
                          areas.map((area) => (
                            <Badge key={area.idArea} variant="outline" className="text-xs">
                              {area.nmArea}
                            </Badge>
                          ))
                        )}
                      </div>
                    </TableCell>
                    <TableCell>{tema.nrDiasPrazo}</TableCell>
                    <TableCell>{tema.tpContagem}</TableCell>
                    <TableCell>{new Date(tema.dtCadastro).toLocaleDateString('pt-BR')}</TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end space-x-2">
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleEditTema(tema)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleDeleteTema(tema.idTema)}
                          className="text-red-500 hover:text-red-700"
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                        <DropdownMenu>
                          <DropdownMenuTrigger asChild>
                            <Button variant="ghost" className="h-8 w-8 p-0">
                              <MoreHorizontal className="h-4 w-4" />
                              <span className="sr-only">Abrir menu</span>
                            </Button>
                          </DropdownMenuTrigger>
                          <DropdownMenuContent align="end">
                            <DropdownMenuItem onClick={() => handleEditTema(tema)}>
                              <Edit className="mr-2 h-4 w-4" />
                              Editar
                            </DropdownMenuItem>
                            <DropdownMenuItem
                              onClick={() => handleDeleteTema(tema.idTema)}
                              className="text-destructive"
                            >
                              <Trash2 className="mr-2 h-4 w-4" />
                              Excluir
                            </DropdownMenuItem>
                          </DropdownMenuContent>
                        </DropdownMenu>
                      </div>
                    </TableCell>
                  </TableRow>
                );
              })
            )}
          </TableBody>
        </Table>
      </div>

      {/* Tema Modal */}
      {showTemaModal && (
        <TemaModal
          tema={selectedTema}
          isOpen={selectedTema !== null}
          onClose={() => {
            setShowTemaModal(false);
            setSelectedTema(null);
          }}
          onSave={handleSaveTema}
        />
      )}
    </div>
  );
}

'use client'

import { useState } from 'react'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Badge } from '@/components/ui/badge'
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table'
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Search, Plus, MoreHorizontal, Edit, Trash2 } from 'lucide-react'
import { Tema } from '@/lib/types'
import { mockTemas, mockAreas } from '@/lib/mockData'
import { TemaModal } from './TemaModal'

interface TemasTableProps {
  onEdit?: (tema: Tema) => void
  onDelete?: (temaId: string) => void
}

export function TemasTable({ onEdit, onDelete }: TemasTableProps) {
  const [temas, setTemas] = useState<Tema[]>(mockTemas)
  const [searchTerm, setSearchTerm] = useState('')
  const [isModalOpen, setIsModalOpen] = useState(false)
  const [editingTema, setEditingTema] = useState<Tema | null>(null)

  const filteredTemas = temas.filter(tema =>
    tema.nmTema.toLowerCase().includes(searchTerm.toLowerCase()) ||
    tema.dsTema.toLowerCase().includes(searchTerm.toLowerCase())
  )

  const getAreasForTema = (idAreas: string[]) => {
    return mockAreas.filter(area => idAreas.includes(area.idArea))
  }

  const handleEdit = (tema: Tema) => {
    setEditingTema(tema)
    setIsModalOpen(true)
    onEdit?.(tema)
  }

  const handleDelete = (temaId: string) => {
    setTemas(prev => prev.filter(tema => tema.idTema !== temaId))
    onDelete?.(temaId)
  }

  const handleSave = (tema: Tema) => {
    if (editingTema) {
      // Update existing tema
      setTemas(prev => prev.map(t => t.idTema === tema.idTema ? tema : t))
    } else {
      // Add new tema
      setTemas(prev => [...prev, tema])
    }
    setEditingTema(null)
  }

  const handleNewTema = () => {
    setEditingTema(null)
    setIsModalOpen(true)
  }

  const handleCloseModal = () => {
    setIsModalOpen(false)
    setEditingTema(null)
  }

  return (
    <div className="space-y-4">
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Temas</CardTitle>
            <Button onClick={handleNewTema} className="flex items-center gap-2">
              <Plus className="h-4 w-4" />
              Novo Tema
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="flex items-center space-x-2 mb-4">
            <div className="relative flex-1">
              <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
              <Input
                placeholder="Buscar temas..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-8"
              />
            </div>
          </div>

          <div className="rounded-md border">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Nome</TableHead>
                  <TableHead>Descrição</TableHead>
                  <TableHead>Áreas Relacionadas</TableHead>
                  <TableHead className="text-center">Prazo</TableHead>
                  <TableHead className="text-center">Tipo Contagem</TableHead>
                  <TableHead className="text-center">Ações</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredTemas.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={6} className="text-center py-8 text-muted-foreground">
                      {searchTerm ? 'Nenhum tema encontrado.' : 'Nenhum tema cadastrado.'}
                    </TableCell>
                  </TableRow>
                ) : (
                  filteredTemas.map((tema) => {
                    const areas = getAreasForTema(tema.idAreas)
                    return (
                      <TableRow key={tema.idTema}>
                        <TableCell className="font-medium">{tema.nmTema}</TableCell>
                        <TableCell className="max-w-xs">
                          <div className="truncate" title={tema.dsTema}>
                            {tema.dsTema}
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="flex flex-wrap gap-1 max-w-sm">
                            {areas.length === 0 ? (
                              <span className="text-muted-foreground text-sm">Nenhuma área</span>
                            ) : (
                              areas.map((area) => (
                                <Badge key={area.idArea} variant="outline" className="text-xs">
                                  {area.nmArea}
                                </Badge>
                              ))
                            )}
                          </div>
                        </TableCell>
                        <TableCell className="text-center">
                          {tema.nrDiasPrazo} dias
                        </TableCell>
                        <TableCell className="text-center">
                          <Badge variant={tema.tpContagem === 'UTEIS' ? 'default' : 'secondary'}>
                            {tema.tpContagem === 'UTEIS' ? 'Úteis' : 'Corridos'}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-center">
                          <DropdownMenu>
                            <DropdownMenuTrigger asChild>
                              <Button variant="ghost" className="h-8 w-8 p-0">
                                <MoreHorizontal className="h-4 w-4" />
                                <span className="sr-only">Abrir menu</span>
                              </Button>
                            </DropdownMenuTrigger>
                            <DropdownMenuContent align="end">
                              <DropdownMenuItem onClick={() => handleEdit(tema)}>
                                <Edit className="mr-2 h-4 w-4" />
                                Editar
                              </DropdownMenuItem>
                              <DropdownMenuItem 
                                onClick={() => handleDelete(tema.idTema)}
                                className="text-destructive"
                              >
                                <Trash2 className="mr-2 h-4 w-4" />
                                Excluir
                              </DropdownMenuItem>
                            </DropdownMenuContent>
                          </DropdownMenu>
                        </TableCell>
                      </TableRow>
                    )
                  })
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      <TemaModal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        onSave={handleSave}
        tema={editingTema}
      />
    </div>
  )
}

